import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Alert,
  Dimensions,
  Animated,
} from 'react-native';
import { StackNavigationProp } from '@react-navigation/stack';
import { LinearGradient } from 'expo-linear-gradient';
import * as Haptics from 'expo-haptics';
import AsyncStorage from '@react-native-async-storage/async-storage';

import { RootStackParamList } from '../types/navigation';
import { CYBERPUNK_COLORS } from '../constants/index';
import { BiometricService } from '../services/BiometricService';
import { MnemonicEncryptionService } from '../services/MnemonicEncryptionService';

type WelcomeScreenNavigationProp = StackNavigationProp<RootStackParamList, 'Welcome'>;

interface Props {
  navigation: WelcomeScreenNavigationProp;
}

const { width, height } = Dimensions.get('window');

const WelcomeScreen: React.FC<Props> = ({ navigation }) => {
  const [fadeAnim] = useState(new Animated.Value(0));
  const [slideAnim] = useState(new Animated.Value(50));
  const [glowAnim] = useState(new Animated.Value(0));
  const [hasExistingWallet, setHasExistingWallet] = useState(false);

  useEffect(() => {
    console.log('WelcomeScreen mounted');

    // Animated intro
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 1500,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]).start();

    // Glow animation
    Animated.loop(
      Animated.sequence([
        Animated.timing(glowAnim, {
          toValue: 1,
          duration: 2000,
          useNativeDriver: true,
        }),
        Animated.timing(glowAnim, {
          toValue: 0,
          duration: 2000,
          useNativeDriver: true,
        }),
      ])
    ).start();

    // Only check wallet on mobile, not on web
    if (typeof window === 'undefined') {
      console.log('Running on mobile, checking existing wallet...');
      checkExistingWallet();
    } else {
      console.log('Running on web, skipping wallet check to prevent auto-redirect');
      setHasExistingWallet(false);
    }
  }, []);

  const checkExistingWallet = async () => {
    try {
      console.log('Checking existing wallet...');
      // Check if wallet already exists in secure storage
      const walletExists = await checkWalletInStorage();
      console.log('Wallet exists:', walletExists);
      setHasExistingWallet(walletExists);
    } catch (error) {
      console.error('Failed to check existing wallet:', error);
      setHasExistingWallet(false);
    }
  };

  // Check wallet existence in secure storage
  const checkWalletInStorage = async (): Promise<boolean> => {
    try {
      console.log('Checking wallet storage...');

      // Skip on web to prevent auto-redirect
      if (typeof window !== 'undefined') {
        console.log('Skipping storage check on web');
        return false;
      }

      const keys = [
        'wallet_encrypted_mnemonic',
        'wallet_public_key',
        'wallet_private_key',
        'current_wallet_address'
      ];

      for (const key of keys) {
        const data = await AsyncStorage.getItem(key);
        if (data) {
          console.log('Found wallet data for key:', key);
          return true;
        }
      }

      // Check biometric config (skip on web)
      if (typeof window === 'undefined') {
        const biometricService = BiometricService.getInstance();
        const biometricConfig = await biometricService.getBiometricConfig();
        if (biometricConfig.isEnabled) {
          console.log('Biometric enabled');
          return true;
        }
      }

      console.log('No existing wallet found');
      return false;
    } catch (error) {
      console.error('Failed to check wallet storage:', error);
      return false;
    }
  };

  const handleCreateWallet = async () => {
    try {
      console.log('Create Wallet button pressed');
      console.log('Navigation object:', navigation);

      // Check if we're on web
      if (typeof window !== 'undefined') {
        console.log('Running on web platform');
      }

      // Try haptics (may not work on web)
      try {
        await Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
        console.log('Haptics triggered');
      } catch (hapticError) {
        console.warn('Haptics failed (expected on web):', hapticError);
      }

      console.log('Navigating to SetupWallet...');
      navigation.navigate('SetupWallet');
      console.log('Navigation completed');
    } catch (error) {
      console.error('Create Wallet failed:', error);
      // Fallback: try direct navigation
      try {
        navigation.navigate('SetupWallet');
      } catch (navError) {
        console.error('Direct navigation also failed:', navError);
      }
    }
  };

  const handleRestoreWallet = async () => {
    try {
      console.log('Restore Wallet button pressed');

      // Try haptics (may not work on web)
      try {
        await Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
        console.log('Haptics triggered');
      } catch (hapticError) {
        console.warn('Haptics failed (expected on web):', hapticError);
      }

      console.log('Navigating to RestoreWallet...');
      navigation.navigate('RestoreWallet');
      console.log('Navigation completed');
    } catch (error) {
      console.error('Restore Wallet failed:', error);
      // Fallback: try direct navigation
      try {
        navigation.navigate('RestoreWallet');
      } catch (navError) {
        console.error('Direct navigation also failed:', navError);
      }
    }
  };

  const handleUnlockWallet = async () => {
    try {
      await Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);

      // Authenticate with biometric
      const biometricService = BiometricService.getInstance();
      const result = await biometricService.authenticateWithBiometric(
        'Access your wallet'
      );

      if (result.success) {
        await Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
        navigation.navigate('WalletHome');
      } else {
        await Haptics.notificationAsync(Haptics.NotificationFeedbackType.Error);
        Alert.alert('Authentication Failed', result.error || 'Please try again');
      }
    } catch (error) {
      console.error('Unlock failed:', error);
      Alert.alert('Error', 'Failed to unlock wallet');
    }
  };

  return (
    <LinearGradient
      colors={[CYBERPUNK_COLORS.background, '#1a1f3a', '#0a0e27']}
      style={styles.container}
    >
      {/* Animated background elements */}
      <Animated.View
        style={[
          styles.glowCircle,
          {
            opacity: glowAnim,
            transform: [
              {
                scale: glowAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [0.8, 1.2],
                }),
              },
            ],
          },
        ]}
      />

      <Animated.View
        style={[
          styles.content,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }],
          },
        ]}
      >
        {/* Logo and title */}
        <View style={styles.header}>
          <Text style={styles.logo}>⚡</Text>
          <Text style={styles.title}>VALKYRIE</Text>
          <Text style={styles.subtitle}>Advanced Cardano Wallet</Text>
        </View>

        {/* Features list */}
        <View style={styles.features}>
          <FeatureItem
            icon="🔒"
            title="Encrypted Mnemonic"
            description="Your seed phrase is encrypted with your personal password"
          />
          <FeatureItem
            icon="👆"
            title="One-Touch Payments"
            description="Biometric authentication for quick transactions"
          />
          <FeatureItem
            icon="📡"
            title="Offline Transactions"
            description="Sign transactions offline and transfer via Bluetooth"
          />
        </View>

        {/* Action buttons */}
        <View style={styles.buttonContainer}>
          {hasExistingWallet ? (
            <CyberpunkButton
              title="UNLOCK WALLET"
              onPress={handleUnlockWallet}
              primary
              icon="🔓"
            />
          ) : (
            <>
              <CyberpunkButton
                title="CREATE WALLET"
                onPress={handleCreateWallet}
                primary
                icon="⚡"
              />
              <CyberpunkButton
                title="RESTORE WALLET"
                onPress={handleRestoreWallet}
                icon="🔄"
              />
            </>
          )}
        </View>

        {/* Security notice */}
        <View style={styles.notice}>
          <Text style={styles.noticeText}>
            🛡️ Your wallet uses military-grade encryption
          </Text>
          <Text style={styles.noticeSubtext}>
            Only you can access your funds with your personal password
          </Text>
        </View>
      </Animated.View>
    </LinearGradient>
  );
};

// Feature item component
const FeatureItem: React.FC<{
  icon: string;
  title: string;
  description: string;
}> = ({ icon, title, description }) => (
  <View style={styles.featureItem}>
    <Text style={styles.featureIcon}>{icon}</Text>
    <View style={styles.featureText}>
      <Text style={styles.featureTitle}>{title}</Text>
      <Text style={styles.featureDescription}>{description}</Text>
    </View>
  </View>
);

// Cyberpunk styled button
const CyberpunkButton: React.FC<{
  title: string;
  onPress: () => void;
  primary?: boolean;
  icon?: string;
}> = ({ title, onPress, primary = false, icon }) => (
  <TouchableOpacity
    style={[styles.button, primary && styles.buttonPrimary]}
    onPress={onPress}
    activeOpacity={0.8}
  >
    <LinearGradient
      colors={
        primary
          ? [CYBERPUNK_COLORS.primary, CYBERPUNK_COLORS.accent]
          : [CYBERPUNK_COLORS.surface, CYBERPUNK_COLORS.border]
      }
      style={styles.buttonGradient}
    >
      {icon && <Text style={styles.buttonIcon}>{icon}</Text>}
      <Text style={[styles.buttonText, primary && styles.buttonTextPrimary]}>
        {title}
      </Text>
    </LinearGradient>
  </TouchableOpacity>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  glowCircle: {
    position: 'absolute',
    top: height * 0.2,
    left: width * 0.3,
    width: width * 0.4,
    height: width * 0.4,
    borderRadius: width * 0.2,
    backgroundColor: CYBERPUNK_COLORS.primary + '20',
    shadowColor: CYBERPUNK_COLORS.primary,
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 0.5,
    shadowRadius: 20,
  },
  content: {
    flex: 1,
    paddingHorizontal: 24,
    paddingTop: 80,
    paddingBottom: 40,
  },
  header: {
    alignItems: 'center',
    marginBottom: 60,
  },
  logo: {
    fontSize: 60,
    marginBottom: 16,
  },
  title: {
    fontSize: 32,
